{
  "manifest_version": "0.4",
  "name": "langlearn-tts",
  "display_name": "Language Learning TTS",
  "version": "0.6.2",
  "description": "Text-to-speech for language learning — synthesize vocabulary, phrases, and bilingual pairs with ElevenLabs, AWS Polly, or OpenAI.",
  "long_description": "Synthesize text to MP3 audio files for language learning. Supports single text, batch, bilingual pairs, and batch pairs with configurable voice, speed, and pause settings. ffmpeg is needed for pair synthesis and batch merge; single text synthesis works without it.\n\nThis extension requires one of three TTS providers: ElevenLabs (premium, 70+ languages), OpenAI (multilingual), or AWS Polly (28 languages). To use ElevenLabs or OpenAI, paste your API key below. To use AWS Polly, run `aws configure` in a terminal — no key is needed here.",
  "author": {
    "name": "JF",
    "email": "jmf@pobox.com",
    "url": "https://github.com/punt-labs"
  },
  "repository": {
    "type": "git",
    "url": "https://github.com/punt-labs/langlearn-tts.git"
  },
  "homepage": "https://github.com/punt-labs/langlearn-tts",
  "documentation": "https://github.com/punt-labs/langlearn-tts#readme",
  "support": "https://github.com/punt-labs/langlearn-tts#readme",
  "server": {
    "type": "uv",
    "entry_point": "src/langlearn_tts/server.py",
    "mcp_config": {
      "command": "uv",
      "args": ["run", "--directory", "${__dirname}", "langlearn-tts-server"],
      "env": {
        "LANGLEARN_TTS_OUTPUT_DIR": "${user_config.output_dir}",
        "ELEVENLABS_API_KEY": "${user_config.elevenlabs_api_key}",
        "OPENAI_API_KEY": "${user_config.openai_api_key}"
      }
    }
  },
  "user_config": {
    "elevenlabs_api_key": {
      "type": "string",
      "title": "ElevenLabs API Key",
      "description": "Get one at elevenlabs.io. Provider auto-detected when key is set.",
      "sensitive": true,
      "required": false
    },
    "openai_api_key": {
      "type": "string",
      "title": "OpenAI API Key",
      "description": "Get one at platform.openai.com. Provider auto-detected when key is set.",
      "sensitive": true,
      "required": false
    },
    "output_dir": {
      "type": "directory",
      "title": "Audio Output Directory",
      "description": "Where synthesized MP3 files are saved",
      "default": "${HOME}/langlearn-audio",
      "required": true
    }
  },
  "tools": [
    {
      "name": "synthesize",
      "description": "Synthesize text to an MP3 audio file"
    },
    {
      "name": "synthesize_batch",
      "description": "Synthesize multiple texts to MP3 files, optionally merged"
    },
    {
      "name": "synthesize_pair",
      "description": "Synthesize a bilingual pair (e.g., English + German) with pause"
    },
    {
      "name": "synthesize_pair_batch",
      "description": "Synthesize a batch of bilingual pairs"
    }
  ],
  "privacy_policies": [
    "https://github.com/punt-labs/langlearn-tts/blob/main/PRIVACY.md"
  ],
  "compatibility": {
    "platforms": ["darwin", "linux", "win32"],
    "runtimes": {
      "python": ">=3.13"
    }
  },
  "keywords": ["tts", "text-to-speech", "language-learning", "elevenlabs", "aws-polly", "openai"],
  "license": "MIT"
}

